}

/* Layout */
.card-content {
  display: flex;
  align-items: center;
  justify-content: space-between;
  height: 100%;
}

/* LEFT SIDE */
.left {
  flex: 1;
  display: flex;
  flex-direction: column;
  gap: 6px;
}

/* Category */
.category {
  display: flex;
  align-items: center;
  gap: 6px;
  font-size: 14px;
  font-weight: 500;
