  margin-top: 10px;
}

.primary {
  font-size: 16px;
  font-weight: 500;
  color: #000000;
}

.secondary {
  font-size: 14px;
  color: #666666;
}

/* RIGHT IMAGE */
.right {
  width: 110px;
  height: 110px;
  border-radius: 12px;
  overflow: hidden;
  background: #f1f1f1;
  display: flex;
  align-items: center;
  justify-content: center;
}

.right img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

/* No Image */
.no-image {
  text-align: center;
  font-size: 14px;
  color: #666;
  line-height: 18px;
