import { Component, Input } from '@angular/core';
import { CommonModule } from '@angular/common';
import { MatExpansionModule } from '@angular/material/expansion';
import { MatIconModule } from '@angular/material/icon';
import { MatButtonModule } from '@angular/material/button';

@Component({
  selector: 'upov-panel',
  standalone: true,
  imports: [
    CommonModule,
    MatExpansionModule,
    MatIconModule,
    MatButtonModule
  ],
  templateUrl: './panel.component.html',
  styleUrls: ['./panel.component.css'],
})
export class Panel {
  @Input() panelOpenState = false;
}

styleUrlsData.data:
/* Wrapper */
.panel-wrapper {
  width: 100%;
  padding: 24px;
  background: #F4F6F8;
}

/* Panel */
.custom-panel {
  max-width: 1158px;
  margin: 0 auto;
  border-radius: 8px !important;
  border: 1px solid #E0E0E0;
  box-shadow: none !important;
}

/* Header */
.custom-header {
  padding: 0 !important;
}

/* Main Row */
.main-info-row {
  display: flex;
  align-items: center;
  gap: 32px;
  padding: 16px 24px;
  width: 100%;
}

.info-group {
  display: flex;
  flex-direction: column;
}

/* Push button right */
.submit-btn {
  margin-left: auto;
  background-color: #163A30 !important;
  color: white !important;
  border-radius: 20px !important;
  padding: 0 16px !important;
  display: flex;
  align-items: center;
  gap: 6px;
}

.submit-btn mat-icon {
  color: #E0F048 !important;
}

/* Expanded Area */
.expanded-content {
  padding: 24px;
  background: white;
}

/* Botanical */
.botanical-label {
  display: block;
  margin-bottom: 8px;
}

.botanical-text {
  font-size: 16px;
  font-weight: 600;
  color: #163A30;
  line-height: 1.6;
}

/* Links */
.links-section {
  margin-top: 24px;
  display: flex;
  flex-wrap: wrap;
  gap: 16px;
  align-items: center;
}

.external-link {
  display: flex;
  align-items: center;
  gap: 4px;
  font-size: 13px;
  color: #163A30;
  text-decoration: underline;
  cursor: pointer;
}

/* Editor */
.input-section {
  margin-top: 24px;
}

.rich-text-editor {
  margin-top: 10px;
  border: 1px solid #163A30;
  border-radius: 4px;
}

.editor-toolbar {
  background: #F8F9FA;
  padding: 8px;
  display: flex;
  gap: 12px;
  border-bottom: 1px solid #E0E0E0;
}

textarea {
  width: 100%;
  min-height: 120px;
  border: none;
  padding: 12px;
  resize: vertical;
  outline: none;
}

/* Footer */
.footer-section {
  margin-top: 32px;
  padding-top: 16px;
  border-top: 1px solid #E0E0E0;
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.details-link {
  font-weight: 600;
  text-decoration: underline;
  cursor: pointer;
  color: #163A30;
}

.meta-right {
  text-align: right;
  font-size: 12px;
  color: #9E9E9E;
}

.tg-code {
  font-size: 14px;
  font-weight: 500;
}

/* Typography */
.label-text {
  font-size: 14px;
  color: #757575;
}

.small-label {
  font-size: 12px;
}

.value-text {
  font-size: 18px;
  font-weight: 700;
  color: #163A30;
}

