.tabs-wrapper {
  display: flex;
  gap: 28px;
  font-size: 16px;
  font-weight: 500;
  border-bottom: 1px solid #e0e0e0;
}

.tab {
  position: relative;
  padding: 12px 0;
  cursor: pointer;
  color: #444;
  transition: color 0.2s ease;
}

.tab:hover {
  color: #184d3c;
}

.tab.active {
  color: #184d3c;
}

.tab.active::after {
  content: "";
  position: absolute;
  bottom: -1px;
  left: 0;
  width: 100%;
  height: 3px;
  background: #184d3c;
  border-radius: 2px;
}
