    {
      ref: 'XU_30202500000145',
      application: 'PBR',
      modified: '21/04/2024',
      crop: 'Cucumis melo L.',
      denomination: 'MELON VARIETY',
      breeder: 'ggs'
    }
  ]);

  filterValues: any = {
    ref: '',
    application: '',
    modified: '',
    crop: '',
    denomination: '',
    breeder: ''
  };

  constructor() {
    this.dataSource.filterPredicate = (data: any, filter: string) => {
      const search = JSON.parse(filter);
      return (
        data.ref.toLowerCase().includes(search.ref) &&
        data.application.toLowerCase().includes(search.application) &&
        data.modified.toLowerCase().includes(search.modified) &&
