        data.denomination.toLowerCase().includes(search.denomination) &&
        data.breeder.toLowerCase().includes(search.breeder)
      );
    };
  }

  applyFilter() {
    this.dataSource.filter = JSON.stringify(this.filterValues);
  }
}

